row.process:
:INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.EXECUTION_ENVIRONMENT
!-------------------------------------Logging input parameters------------------------------------------------------------------------------------------------- : INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.HEADER.PARAM_VALIDATION : INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.LOGGING_CONNECTION_DETAILS
: SET &UC4RB_LOGGING_PARAMETER# = "Virtual Machine name" : SET &UC4RB_LOGGING_PARAMETER_VAL# = "&UC4RB_VSP_VMNAME#" : INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.PARAM
: SET &UC4RB_LOGGING_PARAMETER# = "Location" : SET &UC4RB_LOGGING_PARAMETER_VAL# = "&UC4RB_VSP_LOCATION#" : INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.PARAM
: SET &UC4RB_LOGGING_PARAMETER# = "Folder path" : SET &UC4RB_LOGGING_PARAMETER_VAL# = "&UC4RB_VSP_FOLDER_PATH#" : INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.PARAM
:INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.HEADER.EXECUTION :INC PCK.ITPA_SHARED.PRV.INCLUDE.CHANGE_DIRECTORY_TO_AGENT_BIN
!------------------------------------ Powershell script for the action -------------------------------------------------------------------------------- try { : SET &UC4RB_LOGGING_INFO# = "CONNECT CMD: &UC4RB_VSP_CONNECT_CMDLET#" : INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.INFO
! DECRYPT PASSWORD : INC PCK.ITPA_SHARED.PUB.INCLUDE.ATTACH : SET &UC4RB_ENCRYPTED_PASSWORD_TMP# = "&UC4RB_VSP_PASSWORD#" : INC PCK.ITPA_SHARED.PRV.INCLUDE.DECRYPT_PASSWORD
! Execcuting Connect to vCenter cmdlets $vsp_srv_obj = &UC4RB_VSP_CONNECT_CMDLET# -Password $UC4_DECRYPTED_PWD -Force
try{
! ------------------------------------Prepare PowerCLI commandlet----------------------------------------------------------------------------------- ! VMname : INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.ESCAPE_ARG_VALUE &TEMP_OPTION_VALUE# = &UC4RB_VSP_VMNAME# : SET &UC4RB_VSP_GETVM_CMDLET# = "Get-VM -Name '&UC4RB_VSP_VSPHERE_ESC_VALUE#' "
! Location : IF &UC4RB_VSP_LOCATION# <> "" : INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.ESCAPE_ARG_VALUE &TEMP_OPTION_VALUE# = &UC4RB_VSP_LOCATION# : SET &UC4RB_VSP_GETVM_CMDLET# = STR_CAT(&UC4RB_VSP_GETVM_CMDLET#, "-Location '&UC4RB_VSP_VSPHERE_ESC_VALUE#'") : ENDIF
: SET &UC4RB_LOGGING_INFO# = "EXECUTING GET VM CMD: &UC4RB_VSP_GETVM_CMDLET#" : INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.INFO
! Execcuting Get-VM cmdlets $vmlist = &UC4RB_VSP_GETVM_CMDLET#
! Folder Path : IF &UC4RB_VSP_FOLDER_PATH# <> "" : INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.ESCAPE_ARG_VALUE &TEMP_OPTION_VALUE# = &UC4RB_VSP_FOLDER_PATH# $vmlist = $vmlist | where{ validateFolderPath '&UC4RB_VSP_VSPHERE_ESC_VALUE#' $_.FolderId } : ENDIF
write-host 'Total VMs found as per the specified inputs:' $vmlist.count
if($vmlist.count -eq 0){ throw 'No VMs found for the specified filter criteria' }
Restart-VMGuest -VM $vmlist -Confirm:$false
} finally {
try{ ! Disconnect from the server if connection is establish : SET &UC4RB_LOGGING_INFO# = "DISCONNECT CMD: &UC4RB_VSP_DISCONT_CMDLET#" : INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.INFO &UC4RB_VSP_DISCONT_CMDLET# } catch { Write-Host "Unable to disconnect from the server." Write-Host "INFO: $($error[0].Exception.Message)" } }
} catch { $errors = 1 Write-Host "ERROR: $($error[0].Exception.Message)" }
!-----------------------------------Error Handling------------------------------------------------------------------------------------------------------------ !Status messages :SET &UC4RB_LOGGING_INFO# = "******** RESTART VM GUEST ACTION EXECUTED SUCCESSFULLY ********" :SET &UC4RB_LOGGING_ERROR# = "******** RESTART VM GUEST ACTION FAILED ********"
:INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.CHECK_ERRORSTATUS
