row.process:
:INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.EXECUTION_ENVIRONMENT
! -------------------------------------Logging input parameters------------------------------------------------------------------------------------------------- :INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.HEADER.PARAM_VALIDATION :INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.LOGGING_CONNECTION_DETAILS
:INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.HEADER.EXECUTION :INC PCK.ITPA_SHARED.PRV.INCLUDE.CHANGE_DIRECTORY_TO_AGENT_BIN
! ------------------------------------ Powershell script for the action -------------------------------------------------------------------------------- try { ! Connect to the server :SET &UC4RB_LOGGING_INFO# = "CONNECT CMD: &UC4RB_VSP_CONNECT_CMDLET#" :INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.INFO
! DECRYPT PASSWORD :INC PCK.ITPA_SHARED.PUB.INCLUDE.ATTACH :SET &UC4RB_ENCRYPTED_PASSWORD_TMP# = "&UC4RB_VSP_PASSWORD#" :INC PCK.ITPA_SHARED.PRV.INCLUDE.DECRYPT_PASSWORD
! Executing Connect to vCenter cmdlets $connect_obj = &UC4RB_VSP_CONNECT_CMDLET# -Password $UC4_DECRYPTED_PWD -Force try { ! ------------------------------------Prepare PowerCLI commandlet-----------------------------------------------------------------------------------
! LOGGING GET-STAT CMDLETS :if &SRV_TYPE#="BDD" :set &espace_necessaire#=mult(&ESPACE_DISQUE#,2.1) :set &espace_necessaire#=format(&espace_necessaire#) Write-host "######Disk Usage######"
$DatastoreFreeSpace = (Get-VM -Name &VM_NAME_NEW# | Get-Datastore).FreeSpaceGB Write-host "######Free space: $DatastoreFreeSpace######"
If ($DatastoreFreeSpace -gt &espace_necessaire#) { Write-host "Free space is greater than &espace_necessaire#"
} Else {
Write-host "Free space is less than &espace_necessaire#" Write-host "Workflow will be canceled" $errors = 1
}
: else :set &espace_necessaire#=format(&ESPACE_DISQUE#) Write-host "######Disk Usage######" $DatastoreFreeSpace = (Get-VM -Name &VM_NAME_NEW# | Get-Datastore).FreeSpaceGB Write-host "######Free space: $DatastoreFreeSpace######"
If ($DatastoreFreeSpace -gt &espace_necessaire#) { Write-host "Free space is greater than &espace_necessaire#"
} Else {
Write-host "Free space is less than &espace_necessaire#" Write-host "Workflow will be canceled" $errors = 1
} :endif
} finally { try { ! Disconnect from the server if connection is establish :SET &UC4RB_LOGGING_INFO# = "DISCONNECT CMD: &UC4RB_VSP_DISCONT_CMDLET#" :INC PCK.ITPA_SHARED.PRV.INCLUDE.LOGGING.INFO &UC4RB_VSP_DISCONT_CMDLET# } catch { Write-Host "Unable to disconnect from the server." Write-Host "INFO: $($error[0].Exception.Message)" } } } Catch [VMware.VimAutomation.Sdk.Types.V1.ErrorHandling.VimException.VimException] { Write-Host "ERROR: $($error[0].Exception.Message)"
if("ResourceUnavailable" -ne $error[0].categoryinfo.category){ $errors = 1 }
} catch { $errors = 1 Write-Host "ERROR: $($error[0].Exception.Message)" }
! -----------------------------------Error Handling------------------------------------------------------------------------------------------------------------ ! Status messages :SET &UC4RB_LOGGING_INFO# = "******** CLUSTER CHECK EXECUTED SUCCESSFULLY ********" :SET &UC4RB_LOGGING_ERROR# = "******** CLUSTER CHECK ACTION FAILED ********"
:INC PCK.AUTOMIC_VMWARE_VSPHERE.PUB.INCLUDE.CHECK_ERRORSTATUS
